<template>
  <div class="contingency-plan-reserve-plan">
    <div class="main">
      <div class="contingency-plan__title">
        <span>预案</span>
        <span
          class="contingency-plan__title__add"
          @click="addReservePlan('')"
        ></span>
      </div>
    </div>

    <ul class="contingency-plan-reserve-plan__list">
      <li
        :class="['contingency-plan-reserve-plan__list__item', { 'contingency-plan-reserve-plan__list__item--active': idx === index }]"
        v-for="(item,index) in oneList"
        :key="item.id"
        @click="detailHandle(index,item.id)"
      >
        <el-tooltip
          :content="item.name"
          effect="dark"
          placement="top"
        >
          <div class="contingency-plan-reserve-plan__list__item__label">
            {{item.name}}
          </div>
        </el-tooltip>
        <div>
          <i @click="addReservePlan(item)"></i>
          <i @click="delReservePlan(item.id)"></i>
        </div>
      </li>
    </ul>

    <!-- 新增预案类型组 -->
    <add-reserve-plan ref="addReservePlanView" :groupId="groupId"></add-reserve-plan>
  </div>
</template>

<script>
import bus from "@/views/common/js/eventBus";
import AddReservePlan from "./AddReservePlan";

export default {
  props: {
    oneList: {
      type: Array
    }
  },
  components: {
    AddReservePlan
  },
  data() {
    return {
        idx:"",//判断是否选中
        groupId: '',
        currentTreeId: '',//当前选中Id
    };
  },
  mounted() {
      bus.$off("ContingencyPlanId")
      bus.$on("ContingencyPlanId",(id)=>{
      this.groupId = id;
      this.$emit('getContingencyPlan', id);
      })

      // bus.$off("tabStatus")
      // bus.$on("tabStatus",(tabName)=>{
      //   if(tabName==="组织架构"){
      //     bus.$emit("showDetail", this.idx);
      //   }
      // })
  },
  methods: {
    detailHandle(index,id) {
      this.idx = index
      this.currentTreeId= id
      bus.$emit("showDetail", id);
      bus.$emit("getReservePlan",id)
    },
    // 添加预案分组
    addReservePlan(item) {
      if (this.groupId){
        this.$refs.addReservePlanView.getDataList(item);
      }else{
        this.$message.warning('请选择预案类型组')
      }
    },
    //触发删除试图
    delReservePlan(id){
      this.currentTreeId = id;
      this.$refs.addReservePlanView.delView(this.currentTreeId);
    },
  }
};
</script>

