<template>
    <div>

       <div class="wrap">

         <el-timeline>
           <el-timeline-item  placement="top" style="margin-bottom: 20px;" v-for="(item,index) in listAll" :key="index">
             <el-card style="border: 0;"   >
               <!-- <div @mousedown="divDown($event,item)" @mouseup="divUp($event,item)" @mousemove="divMove($event,item)" > -->
               <div @mousedown="drag($event, item)">
                 <div style="display: flex;justify-content: space-between;" >
                   <h4 style="margin: 0;font-weight: bolder;overflow: hidden;text-overflow: ellipsis;white-space: nowrap;">{{item.Time}} <span style="margin-left: 20px;">{{item.name}}</span></h4>
                   <img v-if="msg && eventRecord" style="width: 12px;height: 15px;" src="data:image/png;base64," alt="">
                   <img v-if="!msg" src="data:image/png;base64," alt="">
                 </div>
                 <p style="margin: 0;margin-top: 5px;font-size: 13px;">{{item.title}}</p>
                 <!-- <p v-if="msg && eventRecord" class="detBtns">详情</p> -->
               </div>
             </el-card>
           </el-timeline-item>

         </el-timeline>

       </div>



    </div>
</template>

<script>
  import Bus from '@/utils/bus'
  export default {
    name: 'evenInfo',
    data () {
      return {
        msg: true,
        eventRecord: true,
        type: '',
        listAll: [],
        top: '',
        left: '',
        event: '',
        juli: {},
        mouseDownFlag: false,
        item: ''
      }
    },
    methods: {
      init (data) {
         // data:{
         //   msg:'',
         //   eventRecord:'',
         //   data:[{},{},{}],
         //   type: 1 || 2 || 3
         // }
        this.msg = data.msg
        this.eventRecord = data.eventRecord
        this.type = data.type
        // console.log(this.msg, this.eventRecord, this.type)
        // eslint-disable-next-line eqeqeq
        if (this.type == '1') {
          // 交班记录的数据
          // eslint-disable-next-line eqeqeq
          if (data.data.length != 0) {
            data.data.forEach(function (ele, index) {
              let Time = ele.createTime.split(' ')
              let itmes = Time[0].split('-')[1] + '月' + Time[0].split('-')[2] + '日  ' + Time[1].split(':')[0] + '时' + Time[1].split(':')[1] + '分'
              ele.Time = itmes
              ele.title = ele.content
              ele.name = ele.name
            })
          }
          this.listAll = data.data
          // eslint-disable-next-line eqeqeq
        } else if (this.type == '2') {
          // 事件信息的数据
          console.log(data)
          data.data.forEach(function (ele, index) {
            ele.name = ele.reportName
            let Time = ele.createTime.split(' ')
            let itmes = Time[0].split('-')[1] + '月' + Time[0].split('-')[2] + '日  ' + Time[1].split(':')[0] + '时' + Time[1].split(':')[1] + '分'
            ele.Time = itmes
          })
          this.listAll = data.data
          // eslint-disable-next-line eqeqeq
        } else if (this.type == '3') {
          // 短信记录的数据
          console.log(data)
          data.data.forEach(function (ele, index) {
            ele.title = ele.content
            let Time = ele.createTime.split(' ')
            let itmes = Time[0].split('-')[1] + '月' + Time[0].split('-')[2] + '日  ' + Time[1].split(':')[0] + '时' + Time[1].split(':')[1] + '分'
            ele.Time = itmes
          })
          this.listAll = data.data
        }
      },
      divDown (e, item) {
        e.currentTarget.style.position = 'fixed'
        e.currentTarget.style.zIndex = '9999'
        e.currentTarget.style.top = this.top - e.currentTarget.offsetHeight / 2 + 'px'
        e.currentTarget.style.left = this.left - e.currentTarget.offsetWidth / 2 + 'px'
        this.event = e.currentTarget
        this.mouseDownFlag = true
        this.item = item
      },
      divUp (e, item) {
        this.item = item
        e.currentTarget.style.position = 'static'
      },
      divMove (e, item) {
        e.currentTarget.style.top = this.top - e.currentTarget.offsetHeight / 2 + 'px'
        e.currentTarget.style.left = this.left - e.currentTarget.offsetWidth / 2 + 'px'
        this.item = item
      },

      /**
       * @author tanjinfeng
       * @date 2020-11-25
       * @description 重做拖动功能，并且mounted中的代码全部注释掉了
       */
      drag(event, item) {
        const dom = event.currentTarget
        dom.style.position = 'fixed'
        dom.style.left = event.clientX + 'px'
        dom.style.top = event.clientY + 'px'
        dom.style.cursor = 'move'
        window.document.body.style.userSelect = 'none'

        // 鼠标移动
        const moveEvent = e => {
          dom.style.left = e.clientX + 'px'
          dom.style.top = e.clientY + 'px'
          if (e.clientX >= this.juli.left) {
            Bus.$emit('IFjuli', item)
          }
        }

        // 鼠标松开
        const upEvent = e => {
          dom.style.position = 'static'
          dom.style.cursor = 'default'
          window.document.body.style.userSelect = ''
          window.removeEventListener('mousemove', moveEvent)
          window.removeEventListener('mouseup', upEvent)
        }

        window.addEventListener('mousemove', moveEvent)
        window.addEventListener('mouseup', upEvent)
      },
    },
    mounted () {
      // let that = this
      // document.body.ondragstart= ()=>{ return false }
      // document.documentElement.onmousemove = function (e) {
      //   that.top = e.clientY
      //   that.left = e.clientX
      //   // e.clientY >= window.innerHeight-0.5 || e.clientY =<0.5 || e.clientX >= window.innerWidth-0.5 || e.clientX =< 0.5
      //   if (e.clientY >= window.innerHeight - 0.5 || e.clientY <= 0.5 || e.clientX >= window.innerWidth - 0.5 || e.clientX <= 0.5) {
      //     if (that.event) {
      //       that.event.style.position = 'static'
      //       that.event = ''
      //       // console.log(window.innerWidth)
      //     }
      //   }
      //   return false
      // }
      // document.documentElement.onmouseup = function (e) {
      //   if (that.event) {
      //     that.event.style.position = 'static'
      //     that.event = ''
      //   }
      //   if (that.mouseDownFlag) {
      //     // console.log(that.top, that.left, that.juli)
      //     // eslint-disable-next-line no-mixed-operators)
      //     if (that.top > that.juli.top && that.top < that.juli.bottom && that.left > that.juli.left && that.left < that.juli.right) {
      //       that.item.flag = true
      //       let item = that.item
      //       Bus.$emit('IFjuli', item)
      //       that.item = ''
      //     }
      //   }
      //   that.mouseDownFlag = false
      // }
      Bus.$off('juli')
      Bus.$on('juli', (data) => {
        this.juli = data
      })
    }
  }
</script>

